import Button from "../Button";
import Input from "../Input";
import { Container, Form } from "./styles";
import {useForm} from "react-hook-form"
import * as yup from "yup"
import {yupResolver} from '@hookform/resolvers/yup'
import { useState } from "react"


const Footer = () =>{
    const [form, setForm] = useState(true)
    const forSchema = yup.object().shape({
        name: yup.string().max(18, "Maxímo de 18 dígitos"),
        email: yup.string().required("Email Obrigatótio").email("Email Inválido"),
        cellphone: yup.string().required("Telefone Obrigatótio"),
        company: yup.string(),
      })
    const{register, handleSubmit, formState:{errors}} = useForm({
        resolver: yupResolver(forSchema)
    });


    const handleSubmitFunction = (data) => setForm(data)
    return(
        <Container>

            <h1>Ficou com dúvida?</h1>
            <h1>Nós entramos em contato com você</h1>
            <form>
                <Form onSubmit={handleSubmit(handleSubmitFunction)} className="form">
                    <Input
                    placeholder="Como gostaria de ser chamado"
                    register={register}
                    name="name"
                    error={errors.email?.message}
                    setHeight="70px"
                    setWidth="100%"
                    />
                    <Input
                    placeholder="Em qual empresa você trabalha?"
                    register={register}
                    name="company"
                    error={errors.firstName?.message}
                    setHeight="70px"
                    setWidth="100%"
                    />
                    <Input
                    placeholder="Digite aqui o seu email"
                    register={register}
                    name="email"
                    error={errors.firstName?.message}
                    setHeight="70px"
                    setWidth="100%"
                    />  
                    <Input
                    placeholder="Qual o seu telefone?"
                    register={register}
                    name="cellphone"
                    error={errors.firstName?.message}
                    setHeight="70px"
                    setWidth="100%"
                    />
                    <Button type="submit" width = 'large' setColor='#0165DB' setSize='giant'> Enviar </Button>
                </Form>
                <button onClick={()=>console.log(console.log(form))}>teste</button>
            </form>

        </Container>
    );
};export default Footer
