import styled from "styled-components"

export const Container = styled.button`
    width:  ${(props) => {
    if (props.width === "huge") {
      return "800px";
    } else if (props.width === "giant") {
      return "500px";
    } else if (props.width === "large") {
      return "300px";
    } else if (props.width === "medium") {
      return "225px";
    } else {
      return "150px";
    }
  }};

    background: #2f5d62;
    color: #f5f5f5;
    height: 45px;
    border-radius: 8px;
    border: 2px solid var(--green);
    font-family: "Roboto Mono", monospace;
    margin-top: 16px;
    :hover{
        border:2px solid var(--orange)
    }
    margin-top:50px;
`;
