import Button from "../Button";
import Input from "../Input";
import { Container, Form } from "./styles";
import {useForm} from "react-hook-form"
import * as yup from "yup"
import {yupResolver} from '@hookform/resolvers/yup'
import { useState } from "react"


const Footer = () =>{
    const [form, setForm] = useState(true)
    const forSchema = yup.object().shape({
        nome: yup.string().max(18, "Maxímo de 18 dígitos"),
        email: yup.string().required("Email Obrigatótio").email("Email Inválido"),
        cellphone: yup.string().required("Telefone Obrigatótio"),
        adress: yup.string().required("Endereço Obrigatótio"),
      })
    const{register, handleSubmit, formState:{errors}} = useForm({
        resolver: yupResolver(forSchema)
    });
    const handleSubmitFunction = (data) => setForm(data)

    return(
        <Container>

            <h1>Ficou com dúvida?</h1>
            <h1>Nós entramos em contato com você</h1>
            <form>
                <Form>
                    <Input
                    placeholder="E-mail"
                    register={register}
                    name="email"
                    error={errors.email?.message}
                    setHeight="70px"
                    setWidth="100%"
                    />

                    <Input placeholder="Como gostaria de ser chamado" {...register("nome")}/>
                    <Input placeholder="Em qual empresa você trabalha?"{...register("adress")}/>
                    <Input placeholder="Digite aqui o seu email"{...register("email")}/>
                    <Input placeholder="Qual o seu telefone?"{...register("cellphone")}/>
                    <Button type="submit" width = 'large' setColor='#263252' setSize='giant'> Ver Mais </Button>
                </Form>
            </form>

        </Container>
    );
};export default Footer
